<div class="box box-primary {class}">
    <span class="hidden json_url">{json_url}</span>
    
                                <div class="box-header" style="cursor: move;">
                                    <div style="padding-left: 0px" class="col-md-8 col-sm-8 col-xs-12">
                                       <h3 class="pull-left box-title"><i class="fa fa-bar-chart-o"></i> {title}</h3>
                                    </div>
                                        <div class="box-tools pull-right">
                                            <button type="button" class="btn btn-default btn-circle size" data-container="body" data-toggle="popover" data-placement="left" data-content="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam."><i style="font-size: 17px; " class="fa fa-question"></i></button>
                                        </div>
                                </div>
                                <div class="box-body">
                                    <div style="height: 300px; padding: 0px; position: relative;" id="bar-chart">
                                        <canvas class="flot-base" style="direction: ltr; position: absolute; left: 0px; top: 0px; width: 523px; height: 300px;" width="523" height="300"></canvas>
                                        <div class="flot-text" style="position: absolute; top: 0px; left: 0px; bottom: 0px; right: 0px; font-size: smaller; color: rgb(84, 84, 84);">
                                            <div class="flot-x-axis flot-x1-axis xAxis x1Axis" style="position: absolute; top: 0px; left: 0px; bottom: 0px; right: 0px; display: block;">
                                                <div style="position: absolute; max-width: 87px; top: 283px; left: 24px; text-align: center;" class="flot-tick-label tickLabel">January</div>
                                                <div style="position: absolute; max-width: 87px; top: 283px; left: 110px; text-align: center;" class="flot-tick-label tickLabel">February</div>
                                                <div style="position: absolute; max-width: 87px; top: 283px; left: 204px; text-align: center;" class="flot-tick-label tickLabel">March</div>
                                                <div style="position: absolute; max-width: 87px; top: 283px; left: 295px; text-align: center;" class="flot-tick-label tickLabel">April</div>
                                                <div style="position: absolute; max-width: 87px; top: 283px; left: 383px; text-align: center;" class="flot-tick-label tickLabel">May</div>
                                                <div style="position: absolute; max-width: 87px; top: 283px; left: 469px; text-align: center;" class="flot-tick-label tickLabel">June</div>
                                            </div>
                                            <div class="flot-y-axis flot-y1-axis yAxis y1Axis" style="position: absolute; top: 0px; left: 0px; bottom: 0px; right: 0px; display: block;">
                                                <div style="position: absolute; top: 270px; left: 7px; text-align: right;" class="flot-tick-label tickLabel">0</div>
                                                <div style="position: absolute; top: 203px; left: 7px; text-align: right;" class="flot-tick-label tickLabel">5</div>
                                                <div style="position: absolute; top: 135px; left: 1px; text-align: right;" class="flot-tick-label tickLabel">10</div>
                                                <div style="position: absolute; top: 68px; left: 1px; text-align: right;" class="flot-tick-label tickLabel">15</div>
                                                <div style="position: absolute; top: 0px; left: 1px; text-align: right;" class="flot-tick-label tickLabel">20</div>
                                            </div>
                                        </div>
                                        <canvas class="flot-overlay" style="direction: ltr; position: absolute; left: 0px; top: 0px; width: 523px; height: 300px;" width="523" height="300"></canvas>
                                    </div>
                                </div><!-- /.box-body-->
                            </div>